import { promises as fs } from 'fs';
import path from 'path';

class FileOperationTools {
  constructor(private safeDirectory: string) {
    this.ensureDirectoryExists();
  }

  private async ensureDirectoryExists() {
    try {
      await fs.access(this.safeDirectory);
    } catch {
      await fs.mkdir(this.safeDirectory, { recursive: true });
    }
  }

  private isPathSafe(filePath: string): boolean {
    const resolvedPath = path.resolve(this.safeDirectory, filePath);
    const resolvedSafeDir = path.resolve(this.safeDirectory);
    return resolvedPath.startsWith(resolvedSafeDir + path.sep) || resolvedPath === resolvedSafeDir;
  }

  getTools() {
    return [
      {
        name: 'read_file',
        description: 'Read the contents of a text file',
        inputSchema: {
          type: 'object',
          properties: {
            path: {
              type: 'string',
              description: 'Path to the file to read (relative to safe directory)'
            }
          },
          required: ['path']
        }
      },
      {
        name: 'write_file',
        description: 'Write content to a file',
        inputSchema: {
          type: 'object',
          properties: {
            path: {
              type: 'string',
              description: 'Path to the file to write (relative to safe directory)'
            },
            content: {
              type: 'string',
              description: 'Content to write to the file'
            },
            overwrite: {
              type: 'boolean',
              description: 'Whether to overwrite existing file',
              default: false
            }
          },
          required: ['path', 'content']
        }
      },
      {
        name: 'list_files',
        description: 'List files in a directory',
        inputSchema: {
          type: 'object',
          properties: {
            directory: {
              type: 'string',
              description: 'Directory to list (relative to safe directory)',
              default: '.'
            },
            showHidden: {
              type: 'boolean',
              description: 'Whether to show hidden files',
              default: false
            }
          }
        }
      }
    ];
  }

  async executeTool(name: string, args: any) {
    try {
      switch (name) {
        case 'read_file':
          return await this.readFile(args.path);

        case 'write_file':
          return await this.writeFile(args.path, args.content, args.overwrite);

        case 'list_files':
          return await this.listFiles(args.directory || '.', args.showHidden);

        default:
          throw new Error(`Unknown file operation: ${name}`);
      }
    } catch (error) {
      return {
        content: [
          {
            type: 'text',
            text: `Error: ${error.message}`
          }
        ],
        isError: true
      };
    }
  }

  private async readFile(filePath: string) {
    if (!this.isPathSafe(filePath)) {
      throw new Error('Path is outside safe directory');
    }

    const fullPath = path.join(this.safeDirectory, filePath);

    try {
      const content = await fs.readFile(fullPath, 'utf-8');
      const stats = await fs.stat(fullPath);

      return {
        content: [
          {
            type: 'text',
            text: `File: ${filePath}\n` +
                  `Size: ${stats.size} bytes\n` +
                  `Modified: ${stats.mtime.toISOString()}\n\n` +
                  `Content:\n${content}`
          }
        ]
      };
    } catch (error) {
      if (error.code === 'ENOENT') {
        throw new Error(`File not found: ${filePath}`);
      }
      throw error;
    }
  }

  private async writeFile(filePath: string, content: string, overwrite: boolean = false) {
    if (!this.isPathSafe(filePath)) {
      throw new Error('Path is outside safe directory');
    }

    const fullPath = path.join(this.safeDirectory, filePath);

    // Check if file exists and overwrite is false
    if (!overwrite) {
      try {
        await fs.access(fullPath);
        throw new Error(`File already exists: ${filePath}. Use overwrite: true to replace it.`);
      } catch (error) {
        if (error.code !== 'ENOENT') {
          throw error;
        }
        // File doesn't exist, which is what we want
      }
    }

    // Ensure directory exists
    const directory = path.dirname(fullPath);
    await fs.mkdir(directory, { recursive: true });

    await fs.writeFile(fullPath, content, 'utf-8');

    return {
      content: [
        {
          type: 'text',
          text: `Successfully wrote ${content.length} characters to ${filePath}`
        }
      ]
    };
  }

  private async listFiles(directory: string, showHidden: boolean = false) {
    if (!this.isPathSafe(directory)) {
      throw new Error('Directory is outside safe directory');
    }

    const fullPath = path.join(this.safeDirectory, directory);

    try {
      const entries = await fs.readdir(fullPath, { withFileTypes: true });
      const files = [];

      for (const entry of entries) {
        if (!showHidden && entry.name.startsWith('.')) {
          continue;
        }

        const entryPath = path.join(fullPath, entry.name);
        const stats = await fs.stat(entryPath);

        files.push({
          name: entry.name,
          type: entry.isDirectory() ? 'directory' : 'file',
          size: entry.isFile() ? stats.size : undefined,
          modified: stats.mtime.toISOString()
        });
      }

      return {
        content: [
          {
            type: 'text',
            text: `Files in ${directory}:\n\n` +
                  files.map(f => 
                    `${f.type === 'directory' ? 'ðŸ“' : 'ðŸ“„'} ${f.name}` +
                    (f.size !== undefined ? ` (${f.size} bytes)` : '') +
                    ` - ${f.modified}`
                  ).join('\n')
          }
        ]
      };
    } catch (error) {
      if (error.code === 'ENOENT') {
        throw new Error(`Directory not found: ${directory}`);
      }
      throw error;
    }
  }
}

// Export for use in MCP server
export function registerFileTools(server: Server, safeDirectory: string = './workspace') {
  const fileTools = new FileOperationTools(safeDirectory);
  const tools = fileTools.getTools();

  // Add to existing tools list
  const existingListHandler = server.getRequestHandler?.('tools/list');

  server.setRequestHandler('tools/list', async () => {
    if (existingListHandler) {
      const existing = await existingListHandler();
      return {
        tools: [...existing.tools, ...tools]
      };
    }

    return { tools };
  });

  // Add to existing tool execution handler
  const existingCallHandler = server.getRequestHandler?.('tools/call');

  server.setRequestHandler('tools/call', async (request) => {
    const { name, arguments: args } = request.params;

    const fileToolNames = tools.map(t => t.name);
    if (fileToolNames.includes(name)) {
      return fileTools.executeTool(name, args);
    }

    if (existingCallHandler) {
      return existingCallHandler(request);
    }

    throw new Error(`Unknown tool: ${name}`);
  });
}

