const httpRequestTool = {
  name: 'http_request',
  description: 'Make HTTP requests to external APIs',
  inputSchema: {
    type: 'object',
    properties: {
      url: {
        type: 'string',
        description: 'The URL to request'
      },
      method: {
        type: 'string',
        enum: ['GET', 'POST', 'PUT', 'DELETE', 'PATCH'],
        default: 'GET',
        description: 'HTTP method to use'
      },
      headers: {
        type: 'object',
        description: 'HTTP headers to include',
        additionalProperties: { type: 'string' }
      },
      body: {
        type: 'string',
        description: 'Request body (for POST, PUT, PATCH)'
      },
      timeout: {
        type: 'number',
        default: 30000,
        description: 'Request timeout in milliseconds'
      }
    },
    required: ['url']
  }
};

// HTTP request handler with safety checks
async function executeHttpRequest(args: any) {
  const { url, method = 'GET', headers = {}, body, timeout = 30000 } = args;

  // Security: Validate URL
  try {
    const urlObj = new URL(url);

    // Block localhost and private IPs for security
    if (urlObj.hostname === 'localhost' || 
        urlObj.hostname.startsWith('127.') ||
        urlObj.hostname.startsWith('192.168.') ||
        urlObj.hostname.startsWith('10.')) {
      throw new Error('Requests to local/private networks are not allowed');
    }
  } catch (error) {
    throw new Error(`Invalid URL: ${error.message}`);
  }

  try {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), timeout);

    const response = await fetch(url, {
      method,
      headers: {
        'User-Agent': 'MCP-Server/1.0',
        ...headers
      },
      body: method !== 'GET' && method !== 'HEAD' ? body : undefined,
      signal: controller.signal
    });

    clearTimeout(timeoutId);

    const responseText = await response.text();

    return {
      content: [
        {
          type: 'text',
          text: `HTTP ${method} ${url}\n` +
                `Status: ${response.status} ${response.statusText}\n` +
                `Headers: ${JSON.stringify(Object.fromEntries(response.headers.entries()), null, 2)}\n\n` +
                `Response:\n${responseText}`
        }
      ]
    };
  } catch (error) {
    return {
      content: [
        {
          type: 'text',
          text: `HTTP request failed: ${error.message}`
        }
      ],
      isError: true
    };
  }
}

class LongRunningTool {
  async executeLongOperation(args: any, progressCallback?: (progress: number, message: string) => void) {
    const totalSteps = 100;

    for (let step = 0; step < totalSteps; step++) {
      // Simulate work
      await new Promise(resolve => setTimeout(resolve, 100));

      // Report progress
      if (progressCallback) {
        progressCallback((step + 1) / totalSteps * 100, `Processing step ${step + 1}`);
      }

      // Check for cancellation
      if (this.shouldCancel) {
        throw new Error('Operation cancelled');
      }
    }

    return {
      content: [
        {
          type: 'text',
          text: 'Long operation completed successfully!'
        }
      ]
    };
  }
}

class ConditionalToolProvider {
  constructor(private config: { adminMode: boolean; apiKey?: string }) {}

  getAvailableTools() {
    const tools = [
      // Always available
      {
        name: 'status',
        description: 'Get system status',
        inputSchema: { type: 'object', properties: {} }
      }
    ];

    // Admin-only tools
    if (this.config.adminMode) {
      tools.push({
        name: 'restart_service',
        description: 'Restart a system service',
        inputSchema: {
          type: 'object',
          properties: {
            service: { type: 'string', description: 'Service name' }
          },
          required: ['service']
        }
      });
    }

    // API-dependent tools
    if (this.config.apiKey) {
      tools.push({
        name: 'external_api_call',
        description: 'Call external API',
        inputSchema: {
          type: 'object',
          properties: {
            endpoint: { type: 'string', description: 'API endpoint' }
          },
          required: ['endpoint']
        }
      });
    }

    return tools;
  }
}
