use std::sync::{Arc, Mutex};
use std::collections::HashMap;
use serde::{Serialize, Deserialize};
use uuid::Uuid;
use chrono::{DateTime, Utc};
use tokio::sync::RwLock;

#[derive(Clone, Serialize, Deserialize)]
pub struct AppState {
    pub version: String,
    pub projects: HashMap<String, Project>,
    pub active_project_id: Option<String>,
    pub ui_state: UiState,
    #[serde(skip)]
    pub dirty: Arc<Mutex<bool>>,
}

#[derive(Clone, Serialize, Deserialize)]
pub struct Project {
    pub id: String,
    pub name: String,
    pub directory: String,
    pub sessions: HashMap<String, Session>,
    pub status: ProjectStatus,
    pub created_at: DateTime<Utc>,
    pub last_accessed: DateTime<Utc>,
    pub metadata: ProjectMetadata,
}

#[derive(Clone, Serialize, Deserialize)]
pub struct Session {
    pub id: String,
    pub name: String,
    pub model: String,
    pub system_prompt: Option<String>,
    pub conversations: Vec<Conversation>,
    pub status: SessionStatus,
    pub created_at: DateTime<Utc>,
    pub last_active: DateTime<Utc>,
    pub token_usage: TokenUsage,
}

impl AppState {
    /// Create a new application state with migration support
    pub async fn new() -> Result<Self, StateError> {
        let config_path = Self::get_config_path()?;

        if config_path.exists() {
            // Load existing state and migrate if needed
            let mut state = Self::load_from_disk(&config_path).await?;

            // Check if migration is needed
            if state.needs_migration() {
                state = Self::migrate_state(state).await?;
                state.save_to_disk().await?;
            }

            Ok(state)
        } else {
            // Create new state
            Ok(Self {
                version: env!("CARGO_PKG_VERSION").to_string(),
                projects: HashMap::new(),
                active_project_id: None,
                ui_state: UiState::default(),
                dirty: Arc::new(Mutex::new(false)),
            })
        }
    }

    /// Migrate state from older versions
    async fn migrate_state(mut old_state: Self) -> Result<Self, StateError> {
        let current_version = env!("CARGO_PKG_VERSION");

        // Backup before migration
        let backup_path = Self::get_config_path()?
            .with_extension(format!("backup-{}", old_state.version));
        Self::save_backup(&old_state, &backup_path).await?;

        // Perform migration based on version
        match old_state.version.as_str() {
            "0.1.0" => {
                // Migrate from session-only to project-based structure
                old_state = Self::migrate_v0_1_0_to_v0_2_0(old_state).await?;
            }
            "0.2.0" => {
                // Add prompt library support
                old_state = Self::migrate_v0_2_0_to_v0_3_0(old_state).await?;
            }
            _ => {
                // No migration needed
            }
        }

        old_state.version = current_version.to_string();
        Ok(old_state)
    }

    /// Add a new project with directory validation
    pub async fn add_project(
        &mut self,
        name: String,
        directory: String,
    ) -> Result<String, StateError> {
        // Validate directory exists and is accessible
        let path = std::path::Path::new(&directory);
        if !path.exists() {
            return Err(StateError::InvalidDirectory(directory));
        }

        // Check for duplicate directories
        for project in self.projects.values() {
            if project.directory == directory {
                return Err(StateError::DuplicateProject(directory));
            }
        }

        let project = Project {
            id: Uuid::new_v4().to_string(),
            name,
            directory,
            sessions: HashMap::new(),
            status: ProjectStatus::Active,
            created_at: Utc::now(),
            last_accessed: Utc::now(),
            metadata: ProjectMetadata::default(),
        };

        let project_id = project.id.clone();
        self.projects.insert(project_id.clone(), project);
        self.mark_dirty();

        Ok(project_id)
    }

    /// Add a session to a project with model validation
    pub async fn add_session_to_project(
        &mut self,
        project_id: &str,
        name: String,
        model: String,
        system_prompt: Option<String>,
    ) -> Result<String, StateError> {
        let project = self.projects.get_mut(project_id)
            .ok_or_else(|| StateError::ProjectNotFound(project_id.to_string()))?;

        // Validate model is supported
        if !Self::is_valid_model(&model) {
            return Err(StateError::InvalidModel(model));
        }

        let session = Session {
            id: Uuid::new_v4().to_string(),
            name,
            model,
            system_prompt,
            conversations: Vec::new(),
            status: SessionStatus::Idle,
            created_at: Utc::now(),
            last_active: Utc::now(),
            token_usage: TokenUsage::default(),
        };

        let session_id = session.id.clone();
        project.sessions.insert(session_id.clone(), session);
        project.last_accessed = Utc::now();
        self.mark_dirty();

        Ok(session_id)
    }

    /// Thread-safe state persistence
    pub async fn save_to_disk(&self) -> Result<(), StateError> {
        let config_path = Self::get_config_path()?;

        // Create config directory if it doesn't exist
        if let Some(parent) = config_path.parent() {
            tokio::fs::create_dir_all(parent).await?;
        }

        // Serialize state to JSON
        let json = serde_json::to_string_pretty(self)?;

        // Write atomically using temp file
        let temp_path = config_path.with_extension("tmp");
        tokio::fs::write(&temp_path, json).await?;
        tokio::fs::rename(&temp_path, &config_path).await?;

        // Clear dirty flag
        if let Ok(mut dirty) = self.dirty.lock() {
            *dirty = false;
        }

        Ok(())
    }

    /// Mark state as dirty for auto-save
    fn mark_dirty(&self) {
        if let Ok(mut dirty) = self.dirty.lock() {
            *dirty = true;
        }
    }

    /// Get projects sorted by last accessed
    pub fn get_sorted_projects(&self) -> Vec<&Project> {
        let mut projects: Vec<&Project> = self.projects.values().collect();
        projects.sort_by(|a, b| b.last_accessed.cmp(&a.last_accessed));
        projects
    }

    /// Filter projects by status
    pub fn filter_projects_by_status(&self, status: ProjectStatus) -> Vec<&Project> {
        self.projects
            .values()
            .filter(|p| p.status == status)
            .collect()
    }
}

/// Auto-save handler running in background
pub async fn auto_save_handler(state: Arc<RwLock<AppState>>) {
    let mut interval = tokio::time::interval(std::time::Duration::from_secs(30));

    loop {
        interval.tick().await;

        let should_save = {
            let state_read = state.read().await;
            state_read.dirty.lock().map(|d| *d).unwrap_or(false)
        };

        if should_save {
            let state_read = state.read().await;
            if let Err(e) = state_read.save_to_disk().await {
                eprintln!("Auto-save failed: {}", e);
            } else {
                println!("Auto-saved state");
            }
        }
    }
}

use dioxus::prelude::*;
