use std::collections::HashMap;

#[derive(Clone, Serialize, Deserialize, PartialEq)]
pub struct PromptTemplate {
    pub id: String,
    pub name: String,
    pub category: PromptCategory,
    pub content: String,
    pub variables: Vec<TemplateVariable>,
    pub tags: Vec<String>,
    pub usage_count: u32,
    pub is_favorite: bool,
    pub shortcut: Option<String>,
}

#[derive(Clone, Serialize, Deserialize, PartialEq)]
pub enum PromptCategory {
    Coding,
    Debugging,
    Documentation,
    Testing,
    Refactoring,
    Security,
    Performance,
    Custom,
}

#[derive(Clone, Serialize, Deserialize, PartialEq)]
pub struct TemplateVariable {
    pub name: String,
    pub description: String,
    pub default_value: Option<String>,
    pub required: bool,
}

#[derive(Clone, Serialize, Deserialize)]
pub struct QuickAction {
    pub id: String,
    pub label: String,
    pub icon: String,
    pub prompt: String,
    pub shortcut: String,
    pub color: String,
}

#[component]
pub fn PromptLibrary(
    on_select: EventHandler<PromptTemplate>,
    on_close: EventHandler<()>,
) -> Element {
    let mut search_query = use_signal(|| String::new());
    let mut selected_category = use_signal(|| None::<PromptCategory>);
    let mut show_favorites = use_signal(|| false);
    let mut templates = use_signal(|| load_prompt_templates());

    // Filter templates based on search and category
    let filtered_templates = use_memo(move || {
        let query = search_query.read().to_lowercase();
        let category = selected_category.read().clone();
        let favorites_only = show_favorites.read();

        templates.read()
            .iter()
            .filter(|t| {
                let matches_search = query.is_empty() || 
                    t.name.to_lowercase().contains(&query) ||
                    t.content.to_lowercase().contains(&query) ||
                    t.tags.iter().any(|tag| tag.to_lowercase().contains(&query));

                let matches_category = category.is_none() || 
                    Some(&t.category) == category.as_ref();

                let matches_favorites = !favorites_only || t.is_favorite;

                matches_search && matches_category && matches_favorites
            })
            .cloned()
            .collect::<Vec<_>>()
    });

    // Handle template selection with variable substitution
    let handle_template_select = move |template: PromptTemplate| {
        if template.variables.is_empty() {
            on_select.call(template);
        } else {
            // Show variable input dialog
            spawn(async move {
                let filled_template = show_variable_dialog(template).await;
                if let Some(filled) = filled_template {
                    on_select.call(filled);
                }
            });
        }
    };

    rsx! {
        div {
            class: "prompt-library fixed inset-0 bg-black/50 flex items-center justify-center z-50",
            onclick: move |_| on_close.call(()),

            div {
                class: "library-content bg-slate-800 rounded-lg shadow-xl max-w-4xl w-full max-h-[80vh] flex flex-col",
                onclick: move |e| e.stop_propagation(),

                // Header
                div {
                    class: "library-header p-6 border-b border-slate-700",

                    div {
                        class: "flex items-center justify-between mb-4",
                        h2 { 
                            class: "text-2xl font-bold text-white",
                            "Prompt Library" 
                        }
                        button {
                            class: "text-slate-400 hover:text-white",
                            onclick: move |_| on_close.call(()),
                            "√ó"
                        }
                    }

                    // Search and filters
                    div {
                        class: "flex gap-4",

                        input {
                            class: "flex-1 px-4 py-2 bg-slate-700 text-white rounded-lg",
                            placeholder: "Search templates...",
                            value: "{search_query}",
                            oninput: move |e| search_query.set(e.value())
                        }

                        button {
                            class: "px-4 py-2 rounded-lg transition-colors",
                            class: if *show_favorites.read() { 
                                "bg-yellow-600 text-white" 
                            } else { 
                                "bg-slate-700 text-slate-300 hover:bg-slate-600" 
                            },
                            onclick: move |_| show_favorites.set(!*show_favorites.read()),
                            "‚≠ê Favorites"
                        }
                    }
                }

                // Main content area
                div {
                    class: "library-body flex flex-1 overflow-hidden",

                    // Category sidebar
                    div {
                        class: "category-sidebar w-48 p-4 border-r border-slate-700",

                        h3 { 
                            class: "text-sm font-semibold text-slate-400 mb-2",
                            "Categories" 
                        }

                        button {
                            class: "category-item w-full text-left px-3 py-2 rounded",
                            class: if selected_category.read().is_none() {
                                "bg-slate-700 text-white"
                            } else {
                                "text-slate-300 hover:bg-slate-700/50"
                            },
                            onclick: move |_| selected_category.set(None),
                            "All Templates"
                        }

                        for category in get_all_categories() {
                            button {
                                class: "category-item w-full text-left px-3 py-2 rounded mt-1",
                                class: if selected_category.read().as_ref() == Some(&category) {
                                    "bg-slate-700 text-white"
                                } else {
                                    "text-slate-300 hover:bg-slate-700/50"
                                },
                                onclick: move |_| selected_category.set(Some(category.clone())),
                                "{format_category(&category)}"
                            }
                        }
                    }

                    // Template list
                    div {
                        class: "template-list flex-1 p-4 overflow-y-auto",

                        if filtered_templates.read().is_empty() {
                            div {
                                class: "empty-state text-center py-12 text-slate-400",
                                "No templates found matching your criteria"
                            }
                        } else {
                            div {
                                class: "grid gap-3",

                                for template in filtered_templates.read() {
                                    TemplateCard {
                                        template: template.clone(),
                                        on_select: move |t| handle_template_select(t),
                                        on_toggle_favorite: move |id| {
                                            templates.write().iter_mut()
                                                .find(|t| t.id == id)
                                                .map(|t| t.is_favorite = !t.is_favorite);
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

#[component]
fn TemplateCard(
    template: PromptTemplate,
    on_select: EventHandler<PromptTemplate>,
    on_toggle_favorite: EventHandler<String>,
) -> Element {
    rsx! {
        div {
            class: "template-card bg-slate-700 rounded-lg p-4 hover:bg-slate-600 transition-colors cursor-pointer",
            onclick: move |_| on_select.call(template.clone()),

            div {
                class: "flex items-start justify-between mb-2",

                div {
                    h4 { 
                        class: "font-semibold text-white",
                        "{template.name}" 
                    }

                    div {
                        class: "flex items-center gap-2 mt-1",

                        span {
                            class: "text-xs px-2 py-1 bg-slate-600 rounded",
                            "{format_category(&template.category)}"
                        }

                        if let Some(shortcut) = &template.shortcut {
                            span {
                                class: "text-xs px-2 py-1 bg-blue-600 rounded",
                                "{shortcut}"
                            }
                        }

                        if template.usage_count > 0 {
                            span {
                                class: "text-xs text-slate-400",
                                "Used {template.usage_count} times"
                            }
                        }
                    }
                }

                button {
                    class: "favorite-button text-2xl",
                    onclick: move |e| {
                        e.stop_propagation();
                        on_toggle_favorite.call(template.id.clone());
                    },
                    if template.is_favorite { "‚≠ê" } else { "‚òÜ" }
                }
            }

            p {
                class: "text-sm text-slate-300 mb-2 line-clamp-2",
                "{template.content}"
            }

            if !template.variables.is_empty() {
                div {
                    class: "flex items-center gap-2 text-xs text-slate-400",
                    "Variables: "
                    for var in &template.variables {
                        span {
                            class: "px-2 py-1 bg-slate-800 rounded",
                            "{{{var.name}}}"
                        }
                    }
                }
            }

            if !template.tags.is_empty() {
                div {
                    class: "flex flex-wrap gap-1 mt-2",
                    for tag in &template.tags {
                        span {
                            class: "text-xs px-2 py-1 bg-slate-800 rounded text-slate-400",
                            "#{tag}"
                        }
                    }
                }
            }
        }
    }
}

